import React,{useState} from 'react';
import classes from './CourseSyllabus.module.css';
import {BsChevronDoubleDown, BsChevronDoubleUp} from "react-icons/bs";




const CourseSyllabus=()=>{


const [showSyllabus, changeShowSyllabus]=useState(false);




const showSyllabusHandler = ()=>{

changeShowSyllabus(true);
}


const hideSyllabusHandler = ()=>{

changeShowSyllabus(false);
}





return (

<div className={classes.courseSyllabus}>

   <i> Course Syllabus:  
	{ !showSyllabus && <button onClick={showSyllabusHandler}> Expand <BsChevronDoubleDown className={classes.ExpandSyllIcon}/>  </button> }

	{ showSyllabus && <button onClick={hideSyllabusHandler}> Contract <BsChevronDoubleUp className={classes.ExpandSyllIcon}/>  </button>}
   </i>

	{showSyllabus && <div className={classes.syllabusContent}>


	 <p>  


         <ul>
            <li>State space</li>
            <li>Dirac notation and Representation of State Spaces</li>
            <li>Concept of Kets, Bras and Operators, Expectation Values</li>
	    <li>Superposition Principle,Orthogonality, Completeness, Expansion of State Vector </li> 

	    <li> Non commutating Observables,
Uncertainty Relations, Commutation and Compatibility, Change of basis, Unitary operators.
State function and its interpretation, Expectation Values  </li>


             <li> Matrix Representation of State Vectors and operators, Continuous Basis. Relation between a
State Vector and its Wave function. 
	    </li>


           <li> Solution of the Linear Harmonic Oscillator with
Operator Method, Coherent States  
	   </li>


         </ul>  






	</p>


   </div>}

</div>

);

}

export default CourseSyllabus;
