import React,{useState} from "react";
import classes from "./AboutEditForm.module.css";
import {AiFillCloseCircle} from "react-icons/ai";
import axiosInstance from '../../../../axios';



const AboutEditForm=(props)=>{


const initialFormData = Object.freeze({
                firstname: '',
        });



const [formData, updateFormData] = useState(initialFormData)

const handleChange = (e) => {
                updateFormData({
                        ...formData,
                        [e.target.name]: e.target.value.trim(),
                });
        };





const handleSubmit = (e) => {
		e.preventDefault();
		console.log('formdataBasicAbout: ----',formData);

		axiosInstance
			.put(`/`,{
	"email":"bibhu.phy@gmail.com",
	"username":"bibhu",			
        "firstname": "Bibhuprasad",
        "lastname": "Mahakud",
        "gender": "male",
        "role": "student",
	"position":"K-12",			



			})
			.then((res) => {
				console.log('-----------------',res.data);
			});
	};





return(

<div className={classes.aboutEditFormDivParent}>

  <form className={classes.aboutEditForm} onSubmit={handleSubmit}>
   <button onClick={props.onPress} className={classes.closeFormButton}> <AiFillCloseCircle className={classes.closeButtonIcon}/> </button>



       <div className={classes.firstname_div}>
	  <i>Firstname</i>
          <input
             type="text"
             onChange={handleChange}
             name="firstname"
             className={classes.firstname_field}
             placeholder="firstname"
          />

       </div>

          <input
             type="text"
             onChange={handleChange}
             name="lastname"
             className={classes.lastname_field}
             placeholder="lastname"
          />

           <input
             type="text"
             onChange={handleChange}
             name="gender"
             className={classes.gender_field}
             placeholder="gender"
          />


	  <input
             type="text"
             onChange={handleChange}
             name="role"
             className={classes.role_field}
             placeholder="role"

          />


          <input
             type="date"
             onChange={handleChange}
             name="dateofbirth"
             className={classes.dateofbirth_field}
             placeholder="date of birth"

          />





          <input
             type="text"
             onChange={handleChange}
             name="position"
             className={classes.position_field}
             placeholder="position"

          />












          <button type="submit"  className= {classes.submit_button}><b>Save </b> </button>




  </form>

</div>	
);

}


export default AboutEditForm;
