import React,{useState, CSSProperties} from 'react';
import OutsideAlerter from "../UserHead/OutsideAlerter";
import classes from './CourseEnrollmentRequest.module.css';
import {putcourseenroll} from '../../../../CommonApps/AllAPICalls';
import FadeLoader from "react-spinners/FadeLoader";
const override: CSSProperties = {
  display: "block",
  margin: "0 auto",
  borderColor: "red",
  fontSize:"10px",
};



const CourseEnrollmentRequest=(props)=>{


  //let requesterName=props.userData.id !==null? props.userData.courseenrollment_requests:"";


   const [ approveState, setApproveState] = useState("notLoading"); 
   let color="white";

   const approveHandler=({courseId, requesterId})=>{

     console.log("enroll Info: ", courseId,requesterId);
     setApproveState("Loading");	   
     putcourseenroll({courseId, requesterId, setApproveState, props});

   }


   const rejectHandler=()=>{



   }

    {/*
    <FadeLoader color={color} loading={true} css={override} size={50}   />
    */}






return (

<OutsideAlerter setDropDown={props.setDropDown}>

<div className={classes.courseEnrollmentRequest}>

        <div className={classes.allRequests}>  

      { props.userData.id !==null && props.userData.courseenrollment_requests.length===0 &&

        <div className={classes.oneRequest} style={{color:'grey'}}> There are no active action items </div>
      }



       { props.userData.id !==null && props.userData.courseenrollment_requests.map((request,index)=>{

          let courseId=request.courseId;
	  let requesterId=request.requesterId;   

    return  <div key={index} className={classes.oneRequest}>      
              <p className={classes.textBox1}>  
                   <span className={classes.requesterName}>
                       { request.requester_firstname !=="" &&
		         request.requester_firstname +" "+request.requester_lastname+" "
                       }
                       { request.requester_firstname ==="" &&
                         request.requester_username +" "
                       }





		   </span>
                   wants to enroll in your 
              </p>
              <p className={classes.textBox2}> Course: 
	       {100000+Number(request.courseId)}
	      </p>
              <div className={classes.ApproveRejectBtnDiv}>
                   <button type="button" className={classes.approveButton} onClick={()=>approveHandler({courseId, requesterId})}> 
		         {approveState==="notLoading" && 
                            <FadeLoader color={color} loading={true} css={override} size={50}   />
			 }
		         <b>Approve</b> 
		   </button>
                   <button type="button" className={classes.rejectButton} onClick={rejectHandler}> 
		       <b>Reject</b> 
		   </button>
              </div>
            </div>

         })

      }




        </div>   


</div>

</OutsideAlerter>




);

}

export default CourseEnrollmentRequest;
