import React, { useState } from 'react';
import axiosInstance from '../axios';
import { useHistory } from 'react-router-dom';
import classes from "./Login.module.css"
import LoginHeader from "./LoginHeader";
import LoginFooter from "./LoginFooter";
import logoImage from './BWlogo.JPG'


const Login =(props)=> {

   const history = useHistory();
   const initialFormData = Object.freeze({
		email: '',
		password: '',
	});

   const [formData, updateFormData] = useState(initialFormData);


   const [fieldColor, setFieldColor] = useState({borderColor: "lightgrey",borderWidth:"1px"});


   const handleChange = (e) => {
		updateFormData({
			...formData,
			[e.target.name]: e.target.value.trim(),
		});
	};


	const handleSubmit = (e) => {
		e.preventDefault();
		console.log(formData);

		axiosInstance.post(`token/`, {email: formData.email,password: formData.password,})
			.then((res) => {
				localStorage.setItem('access_token', res.data.access);
				localStorage.setItem('refresh_token', res.data.refresh);
				axiosInstance.defaults.headers['Authorization'] =
					'JWT ' + localStorage.getItem('access_token');
				props.setLoggedIn(true);
				//history.push('/');
				//console.log(res);
				//console.log(res.data);
			});
	};   


   const mouseOver=()=>{

   setFieldColor({borderColor: "var(--themeColor)",borderWidth:"1px"});

   }

   const mouseLeave=()=>{

   setFieldColor({borderColor: "lightgrey",borderWidth:"1px"});

   }

   const mouseEnter=()=>{

   setFieldColor({borderColor: "var(--themeColor)",borderWidth:"2px"});

   }	










return (



<div className={classes.loginDiv}>

  <LoginHeader/>

  <div className={classes.middleDiv}>

     <div className={classes.middleDiv_logo}>  
        <img  className={classes.bwLogo} src={logoImage} alt='edr Logo' /><i>edresearch.in</i>

     </div>

     <div className={classes.middleDiv_title}>  Sign in </div>


    <form onSubmit={handleSubmit} className={classes.loginForm}>
          <input  
	     onMouseOver={mouseOver}  
	     onMouseLeave={mouseLeave}
	     style={fieldColor} 
	     type="text" 
	     onChange={handleChange} 
	     name="email" 
	     className={classes.email_field} 
	     placeholder="Email address" 

	  />

          <input  
	     onMouseOver={mouseOver} 
	     onMouseLeave={mouseLeave}
	     style={fieldColor} 
	     type="password" 
	     onChange={handleChange} 
	     name="password"  
	     className={classes.password_field} 
	     placeholder="Password"
	  />

          <button type="submit"  className= {classes.submit_button}>SIGN IN </button>
      </form>

  </div>


 <LoginFooter/>


</div>





);






}

export default Login;
