import React,{useState,useEffect} from "react";
import classes from "./LeftUserBoxContainer.module.css"
import LeftUserBox from "./LeftUserBox";
import classimg from '../../../../images/groupImage.png';
import user1Image from '../../../../images/girl1.jpeg'
import {getallusers,getuser} from '../../../../CommonApps/AllAPICalls';

import basewebURL from "../../../../basewebURL";


const LeftUserBoxContainer = () =>{



let groupname="Classical Mechanics-I";
let user1="Reena Samant";




const [usersData, setUsersData ] = useState([
   {
        "id": 1,
        "firstname": "Mr Dummy ",
        "lastname": "User",
        "email": "dummy.user@gmail.com",
        "username": "dummy1988",
        "usertype": "student",
        "profile_image": "http://127.0.0.1:8000/media/codingwithmitch/logo_1080_1080.png",
        "registrationid": "WEDR1000001",
        "gender": "male",
        "position": "MTech",
        "dateofbirth": "2022-01-03"
    }


]);


const [data, setData]=useState(  {
        "id": 1,
        "firstname": "Mr Dummy ",
        "lastname": "User",
        "email": "dummy.user@gmail.com",
        "username": "dummy1988",
        "usertype": "student",
        "profile_image": "http://127.0.0.1:8000/media/codingwithmitch/logo_1080_1080.png",
        "registrationid": "WEDR1000001",
        "gender": "male",
        "position": "MTech",
        "dateofbirth": "2022-01-03"
    }
);	




useEffect(() =>{

   getallusers({ setUsersData});
   getuser({setData});

  },[]);



return(

<div className={classes.leftUserBoxContainer}>

<LeftUserBox userImage={classimg} userName={groupname} />


      {/* in future change this logic. This must interfere performance*/}

      {usersData.filter(
           function(user) {
       if (user.id === data.id) {
              return false; 
            }
               return true;
           })


            .map((user,index)=>{



             return    <LeftUserBox key={index} userImage={user.profile_image} userName={user.firstname +" "+user.lastname}  />



           }

        )}







</div>

);

}

export default LeftUserBoxContainer;
