import React,{useState} from "react";
import classes from "./CreateTicketForm.module.css";
import {AiFillCloseCircle} from "react-icons/ai";
import axiosInstance from '../../../../axios';
import logoImage from '../../../../CommonApps/BWlogo.JPG'


const AboutEditForm=(props)=>{



const [data, setData ] = useState({});


/*
useEffect(() =>{

      axiosInstance.get().then((res) => {
                        setData(data=>res.data);
                        console.log('data inside create ticket form',res.data);
                });



  },[]);

*/







const initialFormData = Object.freeze({
                title: "",
	        category: "",
        });



const [formData, updateFormData] = useState(initialFormData)

const handleChange = (e) => {
                updateFormData({
                        ...formData,
                        [e.target.name]: e.target.value.trim(),
                });
        };






const handleSubmit = (e) => {
		e.preventDefault();
		console.log('formdataBasicAbout: ----',formData);

		axiosInstance
			.put(``,{
	"email":data.email,
	"username":data.username,			
        "firstname": formData.firstname,
        "lastname": formData.lastname,
        "gender": formData.gender,
        "role": "student",
	"position":formData.position,			
        "dateofbirth": formData.dateofbirth,


			})
			.then((res) => {
				console.log('-----------------',res.data);
		});
	 window.location.reload(true);
	};





return(

<div className={classes.createTicketFormDivParent}>

  <form className={classes.createTicketForm} onSubmit={handleSubmit}>

     {/*form close button below*/}	
     <div className={classes.closeButtonDiv}>
        <button onClick={props.onPress} className={classes.closeFormButton}> <AiFillCloseCircle className={classes.closeButtonIcon}/> </button>
     </div>	


     {/*logo and field title container below*/}
     <div className={classes.logoAndTitleContainer}>
	  <img  className={classes.bwLogo} src={logoImage} alt='edr Logo' />
           <div className={classes.formTitleDiv}><i>  Create a ticket </i></div>
     </div>




     <div className={classes.name_div}>
	  <div className={classes.name_txt}>Title</div>
	  <div className={classes.name_inputDiv} >
	    <input
              type="text"
              onChange={handleChange}
              name="title"
              className={classes.title_field}
              placeholder="Enter a title for the ticket"
	      defaultValue=""
            />
         </div>
     </div>




    <div className={classes.genpos_div}>	

      <div className={classes.prioity_div}>	
	<div className={classes.name_txt}>Category</div>
        <select name="category"  onChange={handleChange} type="text" className={classes.genpos_field} defaultValue={"priorityDefault"} >
          <option value="priorityDefault" disabled>Choose category</option>
	  <option value="Scalar">Scalar</option>
          <option value="Newton">Newton</option>
        </select>
      </div>



    </div>









     <div className={classes.submitButtonDiv}>
          <button type="submit"  className= {classes.submit_button} ><b>Create </b> </button>

     </div>


  </form>

</div>	
);

}


export default AboutEditForm;
