const NoticeBox =(props)=>{







return (

<div className={classes.noticeBox}>


<div className={classes.topSection}> 
	<i className={classes.noticeID}>Notice ID: EDR-10122021</i>  
	<i className={classes.creationTime}> Created at: 2pm , 27th Jan 2020</i>  

</div>


<div className={classes.midSection}>  All classes are cancelled on the month June 2022 due to covid ourbreak! Enjoy the holidays.</div>

<div className={classes.buttomSection}> 

<button className={classes.noticeBoxButtonView}> detail view</button>

<button className={classes.noticeBoxButtonRead}> 

 { !props.read &&	<i>mark as read</i> }

</button>

<button className={classes.noticeBoxButtonHide}> hide</button>


</div>	




</div>

);

}


