import React,{useState, useEffect} from "react";
import classes from "./CreateCourseForm.module.css";
import {AiFillCloseCircle} from "react-icons/ai";
import logoImage from '../../../../../../CommonApps/BWlogo.JPG'
import {OptionField,OptionFieldSubmitValue,
	  OptionFieldSecondaryObjs, ParagraphField,
	  TextInput, DateField,
	  TimeField,NumberInput,DayField} from './FormInputObjects';
import {getclassobjectbyId, getclassrank,createcourse,getuser,getsubjectsfromclassandboard,createnewclass} from '../../../../../../CommonApps/AllAPICalls';
import FadeLoader from "react-spinners/FadeLoader";
import { css } from "@emotion/react";


const override = css`
  display: block;
  margin: 0 auto;
  border-color: red;
`;



const CreateCourseForm=(props)=>{


let [loading, setLoading] = useState(true);
let [color, setColor] = useState(" var(--themeColor)");












const [data, setData] = useState({});
const [classId, setClassId]=useState(0);
const [classObject, setClassObject ] = useState({
    "id": 1,
    "name": "Class-11",
    "boardofeducation": []
});


const [classRank, setClassRank] = useState([{"id": 99999999,"name": "Unable to fetch ticket category"}]);

const [selectedBoardName, setSelectedBoardName] = useState("");

const [subjectsObject,setSubjectsObject] = useState([{name:null}]);


const [formSubmitted,setFormSubmitted] = useState(false);



  useEffect(() =>{
      getclassobjectbyId({classId ,setClassObject});
  },[classId]);

  // console.log('class Obj outside: ',classObject.boardofeducation);

  useEffect(() =>{
     getclassrank({setClassRank});
  },[])


  useEffect(()=>{
     getuser({setData});
  },[]);


 useEffect(()=>{

     let boardId=selectedBoardName;
     getsubjectsfromclassandboard({classId,boardId,setSubjectsObject});
 },[classId, selectedBoardName]);


//console.log('subjects',subjectsObject);


const initialFormData = Object.freeze({


        courseShortName: "",
        courseFullName: "",
        courseGlobalCode: "321221",
        courseLocalCode: "",
        courseStatus: "ongoing",
        courseStartDate: "",
        courseEndDate: "",

        classname: null,
        educationboard: "",
        subject: "",

        abouttheCourse: "",
        instituteName: "",
        instituteCity: "BBSR",
        instituteCountry: "India",

        });



const [formData, updateFormData] = useState(initialFormData)

const handleChange1 = (e) => {
        //console.log('classname', e.target.value);
	setClassId(classId=>e.target.value);
        updateFormData({
                        ...formData,
                        [e.target.name]: e.target.value.trim(),
                });
        };




const handleChange2 = (e) => {
	setSelectedBoardName(e.target.value);
	updateFormData({
                        ...formData,
                        [e.target.name]: e.target.value.trim(),
                });

}



const handleChange = (e) => {
        updateFormData({
                        ...formData,
                        [e.target.name]: e.target.value.trim(),
                });
}



const handleChangeS=()=>{



}



const [showForm, setShowForm] = useState(true);



const [showSingleClassForm, setSingleClassForm] = useState(true);




const handleSubmit = (e) => {
	   e.preventDefault();
	   //setShowForm(false);

          //if(formData.classname===null){
	  //	  alert('please enter classname');
          //		  return null;
	  //}



         createnewclass({formData});
	 //alert("Successfully submitted");

         //props.onPress();

         //setFormSubmitted(!formSubmitted);



	};


    const [formButtonStyle,setFormButtonStyle]=useState({
      backgroundColor:'lightgrey',
      color:'grey'	    

    });


   const singleClassHandler=()=>{
   setSingleClassForm(true);
   setFormButtonStyle({
    backgroundColor:'var(--themeColor)',
      color:'white'
   })

   }


  const multiClassHandler=()=>{
  setSingleClassForm(false);
  }





     let intervals=[10,15,20,25,30,40,45,50,60,70,120]

     let dayOptions={"startTime":["00:00","06:30"],"duration":[10,30,45,60]}








return(

<div className={classes.createTicketFormDivParent}>

   {!showForm &&  
	   <div className={classes.createTicketFormLoading}>

	   <FadeLoader color={color} loading={loading} css={""} size={50}  />

	   <div className={classes.submittingDiv}> Creating . . . </div>
           </div>
   }



   <div className={classes.createTicketForm}>

          <div className={classes.closeButtonDiv}>
                 <button onClick={props.onPress} 
	               className={classes.closeFormButton}> 
	                    <AiFillCloseCircle className={classes.closeButtonIcon}/> 
	         </button>
          </div>



          <div className={classes.logoAndTitleContainer}>
                 <img  className={classes.bwLogo} src={logoImage} alt='edr Logo' />
                  <div className={classes.formTitleDiv}><i>  Create  class </i></div>
          </div>




          {  props.selectedCourse.length===0 && <i className={classes.noCourseWarning}>

                  Please goback and select a course before creating class
	          </i>
	  }


          <div className={classes.singleNMultiFormOption}>
              <button className={classes.singleClass} onClick={singleClassHandler} style={formButtonStyle}> <h2>Create a class</h2> </button>
              <button className={classes.multiClass}  onClick={multiClassHandler} style={formButtonStyle}> <h2>Create multiple classes</h2> </button>
          </div>








          {showSingleClassForm &&  <form className={classes.formElement1}>

             <DateField handleChange={handleChange} label="When is your class?" name="classDate" placeholder="Enter a date" />

             <TimeField handleChange={handleChange} label="When class starts" name="classTime" placeholder="Enter a time" />

             <OptionField handleChange={handleChange}  label="Duration of class" name="classduration"  options={intervals} />


           </form>
          }



           {!showSingleClassForm &&  <form className={classes.formElement1}>


	     <div className={classes.multiClassInfo}> First set the classes and time for a week. 
			   Then you can choose to repeat same pattern for several weeks.  </div>  		   

             <DateField handleChange={handleChange} label="Choose start date" name="classDateStart" placeholder="Enter a date" />



             <DayField handleChange={handleChangeS} label="Monday" name="mondayfield" options={dayOptions}/>
             <DayField handleChange={handleChangeS} label="Tuesday" name="tuesdayfield" options={dayOptions}/>
	     <DayField handleChange={handleChangeS} label="Wednesday" name="wednesdayfield" options={dayOptions}/>
	     <DayField handleChange={handleChangeS} label="Thursday" name="thursdayfield" options={dayOptions}/>
	     <DayField handleChange={handleChangeS} label="Friday" name="fridayfield" options={dayOptions}/>
	     <DayField handleChange={handleChangeS} label="Saturday" name="saturdayfield" options={dayOptions}/>
	     <DayField handleChange={handleChangeS} label="Sunday" name="sundayfield" options={dayOptions}/>



           <DateField handleChange={handleChange} label="Choose end date" name="classDateEnd" placeholder="Enter a date" />


             <div className={classes.submitButtonDiv}>
                <button type="submit"  className= {classes.submit_button} ><b>Create </b> </button>
             </div>



           </form>
          }










   </div>



   {   !showForm &&	
  <form className={classes.createTicketForm} onSubmit={handleSubmit}>









       <OptionFieldSecondaryObjs handleChange={handleChange2}  label="Choose Board" name="educationboard"  options={classObject.boardofeducation}/>


       <OptionFieldSubmitValue handleChange={handleChange}  label="Choose Subject" name="subject"  options={subjectsObject}/>       


       <TextInput handleChange={handleChange} label="Assign a Codename" placeholder="Enter a code e.g. QM-231" name="courseLocalCode"/>

       <TextInput handleChange={handleChange} label="Short name of the course" placeholder="e.g. Quantum Mechanics-I" name="courseShortName"/>


       <TextInput handleChange={handleChange} label="Write full name of the course" placeholder="e.g. Elementary Quantum Mechanics of Practical Atomic Systems" name="courseFullName"/>


       <DateField handleChange={handleChange} label="Start date" name="courseStartDate" placeholder="Enter course start date"   />

       <DateField handleChange={handleChange} label="End date" name="courseEndDate" placeholder="Enter course end date"   />

       <ParagraphField label="About the course" name="abouttheCourse"  placeholder="Few words about the course"  handleChange={handleChange} />	


       <TextInput handleChange={handleChange} label="Write name of your institute" placeholder="e.g. IIT BBSR" name="instituteName"/>







