import classes from './CourseViewDashboard_v2.module.css';
import OutsideAlerter from '../../../../../CommonApps/OutsideAlerter'; 

import CourseEditForm from './Forms/CourseEditForm';


const CourseCardDropDown=(props)=>{


  const [showCourseEditForm, setShowCourseEditForm] = useState(false);

  const showCourseEditFormHandler=()=>{
    console.log("hey heye ");	  
    setShowCourseEditForm(true);

  }

  console.log("showCourseEditForm: ", showCourseEditForm);


return (


<OutsideAlerter setDropDown={props.setDropDown}>

<div className={classes.dropdownButtons}>

  {showCourseEditForm && <CourseEditForm/>}

                                    <button type="button" className={classes.dropdownButton} onClick={showCourseEditFormHandler}> edit
                                    </button>
                                    <button type="button" className={classes.dropdownButton} onClick={props.deleteCourseHandler}> delete
                                    </button>
                                </div>



</OutsideAlerter>




);



}

export default CourseCardDropDown;
