import React, { useState, useCallback, useEffect } from "react";
import LoginHeader from "./LoginHeader";
import classes from './Register.module.css';
//import logo from './../CommonApps/BWlogo.JPG';
import {OptionField} from './OptionField';

import {createaccountwithmobileno} from './AllAPICalls';




const Register=()=>{


    const initialFormData = Object.freeze({
                username:'',
                usertype: '',
        });


    const [formData, updateFormData] = useState(initialFormData);

    const [buttonStyle, setButtonStyle]= useState({
      color:'grey',
      backgroundColor:'lightgrey'	    

    });


    const handleChange = useCallback((event) => {

        updateFormData({
                        ...formData,
                        [event.target.name]: event.target.value.trim(),
                });


        console.log("Value: ", event.target.value.length);

        if(event.target.value.length===10){
           setButtonStyle({
            color:'white',
            backgroundColor:'var(--themeColor)'
	   });     
	}else{
        setButtonStyle({
            color:'grey',
            backgroundColor:'lightgrey'
           });

	}

    }, []);






   //console.log("formData", formData);


   const handleSubmit=()=>{

   if(formData.username.length !==10){ 	   
      alert("Enter a 10 digit number");
   }

   if(formData.username.length ===10){

   }

   if(formData.usertype===""){

   alert("Please select usertype");
   }





  }



  //let classRank=["+91","+353","+49"]

 //<OptionField handleChange={handleChange}  label="+91" name="countrycode"  options={classRank}/>



return (

<div className={classes.registerParent}>


  <LoginHeader/>

  <div className={classes.registerDiv}>


    <div className={classes.titleDiv}> <h2>Create an account</h2> </div>

    <div className={classes.switchMethodDiv}>  </div>

    <div className={classes.phoneNumberDiv}>
      <div className={classes.phonetitle}>Enter mobile number </div>

      <div className={classes.enterPhoneDiv}>

          <div className={classes.countryCodes}>

            <img
               alt="India"
	       className={classes.inflag}
               src="http://purecatamphetamine.github.io/country-flag-icons/3x2/IN.svg"/>
               <i> +91</i> 

	  </div> 

           <input
             type="number"
             onChange={handleChange}
             name="username"
             placeholder="Enter mobile number"
             className={classes.usernameInput}
          />



      </div>

      <div className={classes.phonetnc}>By continuing, you agree to our  Terms of Service  and  Privacy Policy  </div>	
    </div>

    <div className={classes.usertypeDiv}> 
        <div className={classes.chooseUserTypeTitle}> Choose your role  </div>

        <div className={classes.userTypeOptions}>
	   <div className={classes.userTypeOptionsInner}>
             <span><input type="radio" value="teacher" name="usertype" className={classes.radioButton} onChange={handleChange}/> Teacher</span>
             <span><input type="radio" value="student" name="usertype" className={classes.radioButton} onChange={handleChange}/> Student</span>
             <span><input type="radio" value="manager" name="usertype" className={classes.radioButton} onChange={handleChange}/> Owner/Manager</span>
           </div>
	</div> 

    </div>

    <div className={classes.submitButtonDiv}>

      <button type="button" onClick={handleSubmit} className={classes.sendOTPButton} style={buttonStyle}> <b>Send OTP</b>  </button>

    </div>


  </div>


</div>

);

}

export default Register;
