import {getuserbyId} from '../../../../CommonApps/AllAPICalls';
//import basewebURL from "../../../../basewebURL";


const LeftUserBox = (props) =>{


    const [userData, setUserData ] = useState({


        "id": 1,
        "firstname": "Mr Dummy",
        "lastname": "User",
        "email": "dummy.user@gmail.com",
        "username": "bibhu",
        "usertype": "teacher",
        "profile_image": "/media/codingwithmitch/logo_1080_1080.png",
        "registrationid": "EDR1000001",
        "gender": "Male",
        "position": "K-12",
        "dateofbirth": "2022-01-21"

     });


   let imageurl = userData.profile_image;


   //console.log("props.group.first",props.group.groupuserObjects);


   let userDisplayObject = props.group.groupuserObjects[0].id !== props.userData.id? props.group.groupuserObjects[0] : props.group.groupuserObjects[1]; 

   console.log("userDisplayObject: ", userDisplayObject);


return(

<div className={classes.leftUserBox}>
    <button className={classes.leftUserBoxButton} onClick={props.onPress} style={props.style} >

         <MateIconBox userImage={userDisplayObject.profile_image} />

         <MateInfoBox userName={userDisplayObject.firstname+" "+userDisplayObject.lastname} />

    </button>
</div>

);

}

export default LeftUserBox;
