import React,{useState, useEffect} from "react";
import classes from "./CreateCourseForm.module.css";
import {AiFillCloseCircle} from "react-icons/ai";
import logoImage from '../../../../../../CommonApps/BWlogo.JPG'
import {OptionField,OptionFieldSubmitValue,
	  OptionFieldSecondaryObjs, ParagraphField,
	  TextInput, DateField,
	  TimeField,NumberInput,DayField} from './FormInputObjects';
import {getclassobjectbyId, createcourse,getuser,getsubjectsfromclassandboard,createnewclass} from '../../../../../../CommonApps/AllAPICalls';
import FadeLoader from "react-spinners/FadeLoader";
import { css } from "@emotion/react";


const override = css`
  display: block;
  margin: 0 auto;
  border-color: red;
`;



const CreateCourseForm=(props)=>{


let [loading, setLoading] = useState(true);
let [color, setColor] = useState(" var(--themeColor)");


const [data, setData] = useState({});




  useEffect(()=>{
     getuser({setData});
  },[]);



////////////////////////Single Class Details/////////////////////////
    const initialFormDataSingleClass = Object.freeze({
	courseId: props.selectedCourse.length > 0 ? props.selectedCourse[0].id : null,
        serialNo: null,
        classStatus: "scheduled",
        classdate: "",
        classtime: "00:00:00",
        duration: null,
        meetingLink: "",
        roomNo: "",
        chapter: null,
        topics: []


        });



    const [formDataSingleClass, updateFormDataSingleClass] = useState(initialFormDataSingleClass);

    const [singleClassTime,setSingleClassTime] = useState("00:00:00");	


    const handleChangeSingleClass = (e) => {

	//console.log("name, value: ", e.target.name,"---",e.target.value);    


        updateFormDataSingleClass({
                        ...formDataSingleClass,
                        [e.target.name]: e.target.value.trim(),
                });
    }




    const [showForm, setShowForm] = useState(true);

    const [showSingleClassForm, setSingleClassForm] = useState(true);

    const handleSubmit = (e) => {
	   e.preventDefault();

          if(formDataSingleClass.classdate===""){
            alert('please enter date');
                        return null;
          }


          if(!("selectedhour" in formDataSingleClass)){
            alert('please select hour');
                        return null;
	  }

          if(!("selectedminute" in formDataSingleClass)){
            alert('please select minutes');
                        return null;
          }

          if(!("selectedampm" in formDataSingleClass)){
            alert('please select am or pm');
                        return null;
          }

          if(formDataSingleClass.duration===null){
            alert('please enter duration');
                        return null;
          }









          let ampm = formDataSingleClass.selectedampm;
	  let minute = formDataSingleClass.selectedminute;
	  let hour = formDataSingleClass.selectedhour;  
          if(ampm==="pm"){
          hour=Number(hour)+12;
	  }

	  let time=hour+':'+minute+':'+'00';  

	  let timestr= time.toString(); 

          formDataSingleClass["classtime"]=timestr;

          console.log("time: ", formDataSingleClass.classtime);


         createnewclass({formDataSingleClass});
	 //alert("Successfully submitted");

         //props.onPress();

         //setFormSubmitted(!formSubmitted);



	};


    const [formButton1Style,setFormButton1Style]=useState({
      backgroundColor:'var(--themeColor)',
      color:'white'	    

    });

    const [formButton2Style,setFormButton2Style]=useState({
      backgroundColor:'lightgrey',
      color:'grey'

    });




    const singleClassHandler=()=>{
       setSingleClassForm(true);
       setFormButton1Style({
         backgroundColor:'var(--themeColor)',
         color:'white'
       });

       setFormButton2Style({
          backgroundColor:'lightgrey',
          color:'grey'
       });
    }


    const multiClassHandler=()=>{
       setSingleClassForm(false);
       setFormButton1Style({
          backgroundColor:'lightgrey',
          color:'grey'
       });

       setFormButton2Style({
          backgroundColor:'var(--themeColor)',
          color:'white'
       });	 
    }


    let intervals=[10,15,20,25,30,40,45,50,60,70,120]



   //Multi Class Details

    const [formDataMultiClass, updateFormDataMultiClass] = useState(initialFormDataSingleClass);

    const handleChange=()=>{

    }

    const [checkedM, setCheckedM] = useState(false);
    const [checkedT, setCheckedT] = useState(false);



    const handleChangeMulti=(e)=>{
    console.log("name, value: ", e.target.name,"---",e.target.value," checkedM",checkedM, "--checkedT",checkedT);
     updateFormDataMultiClass({
                        ...formDataMultiClass,
                        [e.target.name]: e.target.value.trim(),
                });





    }



   console.log("formData Multi: ",formDataMultiClass );








return(

<div className={classes.createTicketFormDivParent}>

   {1===2 &&  
	   <div className={classes.createTicketFormLoading}>

	   <FadeLoader color={color} loading={loading} css={""} size={50}  />

	   <div className={classes.submittingDiv}> Creating . . . </div>
           </div>
   }



   <div className={classes.createTicketForm}>

          <div className={classes.closeButtonDiv}>
                 <button onClick={props.onPress} 
	               className={classes.closeFormButton}> 
	                    <AiFillCloseCircle className={classes.closeButtonIcon}/> 
	         </button>
          </div>



          <div className={classes.logoAndTitleContainer}>
                 <img  className={classes.bwLogo} src={logoImage} alt='edr Logo' />
                  <div className={classes.formTitleDiv}><i>  Create  class </i></div>
          </div>




          {  props.selectedCourse.length===0 && <div className={classes.noCourseWarning}>

                  Please goback and select a course before creating class
	          </div>
	  }


	 { props.selectedCourse.length > 0 &&
          <div className={classes.singleNMultiFormOption}>
              <button className={classes.singleClass} onClick={singleClassHandler} style={formButton1Style}> <h2>Create a class</h2> </button>
              <button className={classes.multiClass}  onClick={multiClassHandler} style={formButton2Style}> <h2>Create multiple classes</h2> </button>
          </div>
         }







          { props.selectedCourse.length > 0 && showSingleClassForm &&  <form className={classes.formElement1} onSubmit={handleSubmit}>

             <DateField handleChange={handleChangeSingleClass} label="*When is your class?" name="classdate" placeholder="Enter a date" />

             <TimeField handleChange={handleChangeSingleClass} 
		     label="*When class starts" 		     
		     selectedhour = "selectedhour"
		     selectedminute = "selectedminute"
		     selectedampm = "selectedampm"
		     placeholder="Enter a time" 
		/>

             <OptionField handleChange={handleChangeSingleClass}  label="*Duration of class" name="duration"  options={intervals} />


              <div className={classes.submitButtonDiv}>
                <button type="submit"  className= {classes.submit_button} ><b>Create </b> </button>
             </div>



           </form>
          }



           {props.selectedCourse.length > 0 && !showSingleClassForm &&  <form className={classes.formElement1}>


	     <div className={classes.multiClassInfo}> First set the classes and time for a week. 
			   Then you can choose to repeat same pattern for several weeks.  </div>  		   

             <DateField handleChange={()=>handleChangeMulti} label="Choose start date" name="classDateStart" placeholder="Enter a date" />




             <DayField handleChange={handleChangeMulti} 
		   label="Monday" 
		   selectedhour = "selectedhourM"
                   selectedminute = "selectedminuteM"
                   selectedampm = "selectedampmM"
		   setChecked = {setCheckedM}
			   />
             <DayField handleChange={handleChangeMulti} 
		   label="Tuesday" 
		   selectedhour = "selectedhourT"
                   selectedminute = "selectedminuteT"
                   selectedampm = "selectedampmT"
		   setChecked = {setCheckedT}
			   />
             {/*			   
	       <DayField handleChange={handleChangeS} label="Wednesday" name="wednesdayfield" />
	       <DayField handleChange={handleChangeS} label="Thursday" name="thursdayfield" />
	       <DayField handleChange={handleChangeS} label="Friday" name="fridayfield" />
	       <DayField handleChange={handleChangeS} label="Saturday" name="saturdayfield" />
	       <DayField handleChange={handleChangeS} label="Sunday" name="sundayfield" />
             */}


           <DateField handleChange={handleChangeMulti} label="Choose end date" name="classDateEnd" placeholder="Enter a date" />


             <div className={classes.submitButtonDiv}>
                <button type="submit"  className= {classes.submit_button} ><b>Create </b> </button>
             </div>



           </form>
          }










   </div>



   {  /* !showForm &&	
  <form className={classes.createTicketForm} onSubmit={handleSubmit}>









       <OptionFieldSecondaryObjs handleChange={handleChange2}  label="Choose Board" name="educationboard"  options={classObject.boardofeducation}/>


       <OptionFieldSubmitValue handleChange={handleChange}  label="Choose Subject" name="subject"  options={subjectsObject}/>       


       <TextInput handleChange={handleChange} label="Assign a Codename" placeholder="Enter a code e.g. QM-231" name="courseLocalCode"/>

       <TextInput handleChange={handleChange} label="Short name of the course" placeholder="e.g. Quantum Mechanics-I" name="courseShortName"/>


       <TextInput handleChange={handleChange} label="Write full name of the course" placeholder="e.g. Elementary Quantum Mechanics of Practical Atomic Systems" name="courseFullName"/>


       <DateField handleChange={handleChange} label="Start date" name="courseStartDate" placeholder="Enter course start date"   />

       <DateField handleChange={handleChange} label="End date" name="courseEndDate" placeholder="Enter course end date"   />

       <ParagraphField label="About the course" name="abouttheCourse"  placeholder="Few words about the course"  handleChange={handleChange} />	


       <TextInput handleChange={handleChange} label="Write name of your institute" placeholder="e.g. IIT BBSR" name="instituteName"/>







    <div className={classes.submitButtonDiv}>
          <button type="submit"  className= {classes.submit_button} ><b>Create </b> </button>

    </div>






  </form>*/}


</div>	
);

}


export default CreateCourseForm;
