import React,{useState,useEffect} from "react";
import classes from "./ChatWindow.module.css"
import ChatUserInfoTopBar from "./ChatUserInfoTopBar";
import ChatScreen from "./ChatScreen";
import ChatInput from "./ChatInput";
import {w3cwebsocket as W3CWebSocket } from 'websocket';




const ChatWindow = () =>{


let roomname='class'



const [state, setState] = useState(

  {
    messages: ['hello'],
    value: 'jai ganesh',
  }
);



const client = new W3CWebSocket('ws://127.0.0.1:8000/ws/chat/class/');



  const handleSubmit = (e) => {
     client.send(JSON.stringify({
      type: "message",
      message: state.value,
    }));
    state.value = ''
    e.preventDefault();
  }





useEffect(()=>{


client.onopen=()=>{

   console.log("web socket client connected");};


   client.onmessage = (message) => {

    const dataFromServer = JSON.parse(message.data);
    console.log('got reply! ', dataFromServer.message);

   if (dataFromServer !== ""){

   setState((state)=>({

	 //  messages:[...state.messages,dataFromServer.message]

         messages: [...state.messages,dataFromServer.message],
         value: 'jai ganesh',


      }));

   }

   console.log(state.messages[0])


     }
},[]);
















return(

<div className={classes.chatWindow}>

<ChatUserInfoTopBar/>

<ChatScreen messages={state.messages}/>



<div className={classes.chatInput}>





  <form className={classes.addCommentForm} onSubmit={handleSubmit}>

                   <div className={classes.inputTextBox} >
                          <textarea
                             type="text"
                             name="comment"
	                     value={state.value}
                             className={classes.inputText_field}
                             placeholder="Write your comment"
	                     onChange={e => {
                             setState({ value: e.target.value });
                              }}
                          />
                    </div>









                 <button type="submit"  className= {classes.submit_button} ><b>Post comment </b> </button>
           </form>












</div>



</div>

);

}

export default ChatWindow;
