import React,{useState} from 'react';
import classes from './TopToolBar.module.css';
import TopToolBox from './TopToolBox';



const TopToolBar =()=>{




const [toolBoxStyle1, setToolBoxStyle1] = useState(
{
	buttonBkgColor:'#ff5349',
        boxBkgColor:'#ffd4d1',
	rightTopBoxColor: 'green',
        buttonText:'Create a course',
	mainText:'TOTAL COURSES',
	mainNumber:'5',
	topRightText:'Ongoing',
	topRightNumber:'3'

});



const [toolBoxStyle2, setToolBoxStyle2] = useState(
{
        buttonBkgColor:'#128C7E',
        boxBkgColor:'#b3fff6',
        rightTopBoxColor: 'green',
        buttonText:'View classes',
        mainText:"TODAY'S CLASSES ",
        mainNumber:'3',
        topRightText:'Attended',
        topRightNumber:'1'

});



const [toolBoxStyle3, setToolBoxStyle3] = useState(
{
        buttonBkgColor:'grey',
        boxBkgColor:'lightgrey',
        rightTopBoxColor: 'green',
        buttonText:'View classes',
        mainText:"TODAY'S CLASSES ",
        mainNumber:'3',
        topRightText:'Attended',
        topRightNumber:'1'

});







const [toolBoxStyle4, setToolBoxStyle4] = useState(
{
        buttonBkgColor:'#83aaf2',
        boxBkgColor:'#e1e9f7',
        rightTopBoxColor: 'grey',
        buttonText:'View Notice Board',
        mainText:'TOTAL NOTICES',
        mainNumber:'15',
        topRightText:'Unread',
        topRightNumber:'6'


});












return (


<div className={classes.topToolBar}>

 <TopToolBox toolBoxStyle={toolBoxStyle1}/>
 <TopToolBox toolBoxStyle={toolBoxStyle2}/>
 <TopToolBox toolBoxStyle={toolBoxStyle3}/>
 <TopToolBox toolBoxStyle={toolBoxStyle4}/>


</div>


);


}

export default TopToolBar;
