import React,{useState, useEffect} from 'react'
import classes from './CourseViewDashboard.module.css';
import {BsFillCheckSquareFill} from 'react-icons/bs';
import { useHistory } from "react-router-dom";
import {BsFillTrashFill, BsPeopleFill} from 'react-icons/bs';
import {BiEdit} from 'react-icons/bi';
import {RiSendPlaneFill} from 'react-icons/ri';
import EnrollForm from './EnrollForm';




const CourseViewDashboard = (props)=>{

 let history = useHistory();

 const [style,setStyle]=useState(props.Course);

 const [enrollForm, showEnrollForm] = useState(false);
 const [enrolled, setEnrolled] = useState(false);


 const moveToSubject=()=>{
  history.push('/dashboard/subject');

 }



const enrollHandler =()=>{

showEnrollForm(true);


}

const closeEnrollFormHandler = ()=>{

showEnrollForm(false);
}


useEffect(()=>{

if(props.Course.enrolledstudents.includes(props.data.id)){
setEnrolled(enrolled=>true);
}

},[props.data.id,props.Course.enrolledstudents]);


console.log("This user is enrolled",enrolled);






return (

<div className={classes.courseViewDashboard}>



   <div className={classes.infoUnitBarParent}  style={{backgroundColor: style.infoBarBkgColor}} onClick={moveToSubject}>
	<div className={classes.infoUnitBar} style={{backgroundColor: style.infoBarBkgColor}}>


          {/*
          <div>SUBJECT: <span className={classes.courseName}>
                       <span className={classes.courseNameText}  style={{color:style.courseNameColor}}> {style.courseName}  </span>
                       <BsCheckCircleFill className={classes.signupStatus} style={{color:style.courseSignUpstatusColor}}/>
                  </span>
          </div>        
          <i className={classes.codeSec}>CODE: <span className={classes.courseCodeName}>CLM 221 (342101)</span></i>
          */}


        <div className={classes.subjectSection}> 
	   <span className={classes.signupIcon}> <BsFillCheckSquareFill/> </span>
	   <span className={classes.subjectTitle}> SUBJECT: </span>
	   <span className={classes.subjectName}>{props.Course.subject}</span>   

	</div>          


	<i className={classes.codeSection}>CODE: <span className={classes.courseCode}>{props.Course.courseLocalCode} ({props.Course.courseGlobalCode})</span></i>




      </div>
   </div>




   <div className={classes.genInfoBar}>
      <div className={classes.designedFor}>
        DESIGNED FOR: <i>{props.Course.designedFor}</i> 
      </div>

      <div className={classes.toolIcons}>

         {enrollForm && <EnrollForm onPress={closeEnrollFormHandler} Course={props.Course}/>}

         <button className={classes.editButton} onClick={enrollHandler}>

	      { enrolled && <i> enrolled </i>}

	      { !enrolled  && <i> enroll</i>}
	     <RiSendPlaneFill className={classes.enrollIcon}/> 
	 </button>
       </div>

   </div>


   <div className={classes.genInfoBar}>
       <div className={classes.status}>
        STATUS: <i> Ongoing</i>
       </div>


   </div>






   <div className={classes.genInfoBar}>
      <div className={classes.instructor}>
        INSTRUCTOR: <i>Prof. Seema Bahinipati</i>
      </div>

      <div className={classes.toolIcons}>
         <button className={classes.editButton}> 
	     <BsPeopleFill/> 
	     <span className={classes.enrolledStuds}><i>{props.Course.enrolledstudents.length}</i></span>
	</button>
       </div>

   </div>

   <div className={classes.genInfoBar}>
      <div className={classes.instructor}>
         UPCOMING CLASS: <i> 9:45 am |  27/02/2022</i>
      </div>


   </div>	



   <div className={classes.genInfoBar}>
      <button className={classes.switchTo}>
        Select 
      </button>

      <div className={classes.viewDetail}>
        <i>View Detail</i>
      </div>




      <div className={classes.toolIcons}>
         <button className={classes.editButton}> <BsFillTrashFill/> </button>
       </div>

   </div>








</div>


);

}

export default CourseViewDashboard;
